mapper.select:
SELECT * FROM (
	SELECT rownum rnum, M.* FROM (
		SELECT
			user_no, user_id, user_name, user_phone
			, user_address, user_birth, user_email, user_join
		FROM member
		ORDER BY user_no DESC
	) M
) MEMBER
WHERE rnum BETWEEN #{ startNo } AND #{ endNo }


	SELECT user_no, user_name, user_id FROM (
		SELECT rownum as rn, user_no, user_name, user_id
		FROM member WHERE rownum <= #{ pageNum } * #{ amount }

	
		
	

	) WHERE rn > (#{ pageNum } -1) * #{ amount }

SELECT * FROM (
	SELECT rownum rnum, N.* FROM (
		SELECT
			notice_no, writer_id, writer_nick, notice_title, notice_content
			, notice_date, notice_hit
		FROM notice
		ORDER BY notice_no DESC
	) N
) NOTICE
WHERE rnum BETWEEN #{ startNo } AND #{ endNo }

SELECT * FROM (
	SELECT rownum rnum, Q.* FROM (
		SELECT
			qna_no, writer_id, writer_nick, qna_title, qna_content, qna_date
		FROM qna
		ORDER BY qna_no DESC
	) Q
) QNA
WHERE rnum BETWEEN #{ startNo } AND #{ endNo }

SELECT * FROM (
	SELECT rownum rnum, I.* FROM (
		SELECT
			inquiry_no, inquiry_tilte, inquiry_content, inquiry_email, inquiry_date
		FROM inquiry
		ORDER BY inquiry_no DESC
	) I
) INQUIRY
WHERE rnum BETWEEN #{ startNo } AND #{ endNo }

mapper.update:
UPDATE notice
SET notice_title = #{ noticeTitle }
	, notice_content = #{ noticeContent }
	, notice_date = SYSDATE
WHERE notice_no = #{ noticeNo }

UPDATE notice
SET notice_hit = notice_hit + 1
WHERE notice_no = #{ noticeNo }

UPDATE qna
SET qna_title = #{ qnaTitle }
	, qna_content = #{ qnaContent }
WHERE qna_no = #{ qnaNo }
