mapper.select:
SELECT * FROM room_list
WHERE user_no = #{userNo}
ORDER BY room_list_no

SELECT * FROM room
	
		room_no = #{roomNo}
	
ORDER BY room_name

SELECT * FROM diary_favorite
WHERE user_no = #{userNo}
ORDER BY room_name

SELECT d.*, m.user_name, r.room_name, c.diary_cate_name, h.diary_hot FROM diary d left outer join member m ON d.user_no = m.user_no LEFT OUTER JOIN room r ON d.room_no = r.room_no LEFT OUTER JOIN diary_category c ON d.diary_cate_no = c.diary_cate_no LEFT OUTER JOIN diary_hot h ON d.room_no = h.room_no WHERE diary_cate_name = '공지사항' AND (
d.room_no = #{item.roomNo}
) ORDER BY d.diary_date

SELECT d.*, m.user_name, r.room_name, c.diary_cate_name, h.diary_hot FROM diary d left outer join member m ON d.user_no = m.user_no LEFT OUTER JOIN room r ON d.room_no = r.room_no LEFT OUTER JOIN diary_category c ON d.diary_cate_no = c.diary_cate_no LEFT OUTER JOIN diary_hot h ON d.room_no = h.room_no WHERE diary_cate_name != '공지사항' AND (
d.room_no = #{item.roomNo}
) AND d.diary_recommend >= h.diary_hot ORDER BY d.diary_date

SELECT d.*, m.user_name, r.room_name, c.diary_cate_name, h.diary_hot FROM diary d left outer join member m ON d.user_no = m.user_no LEFT OUTER JOIN room r ON d.room_no = r.room_no LEFT OUTER JOIN diary_category c ON d.diary_cate_no = c.diary_cate_no LEFT OUTER JOIN diary_hot h ON d.room_no = h.room_no WHERE diary_cate_name != '공지사항' AND (
d.room_no = #{item.roomNo}
) ORDER BY d.diary_date

SELECT * FROM room
WHERE user_no = #{userNo}
ORDER BY room_no
