mapper.select:
SELECT * FROM room_list
WHERE user_no = #{userNo}
ORDER BY room_list_no

SELECT * FROM room
	
		room_no = #{roomNo}
	
ORDER BY room_name

SELECT * FROM diary_favorite
WHERE user_no = #{userNo}
ORDER BY room_name

SELECT d.*, m.user_name, r.room_name, c.diary_cate_name, h.diary_hot FROM diary d left outer join member m ON d.user_no = m.user_no LEFT OUTER JOIN room r ON d.room_no = r.room_no LEFT OUTER JOIN diary_category c ON d.diary_cate_no = c.diary_cate_no LEFT OUTER JOIN diary_hot h ON d.room_no = h.room_no WHERE diary_cate_name = '공지사항' AND (
d.room_no = #{item.roomNo}
) ORDER BY d.diary_date DESC

SELECT d.*, m.user_name, r.room_name, c.diary_cate_name, h.diary_hot FROM diary d left outer join member m ON d.user_no = m.user_no LEFT OUTER JOIN room r ON d.room_no = r.room_no LEFT OUTER JOIN diary_category c ON d.diary_cate_no = c.diary_cate_no LEFT OUTER JOIN diary_hot h ON d.room_no = h.room_no WHERE (
d.room_no = #{item.roomNo}
) AND d.diary_recommend >= h.diary_hot ORDER BY d.diary_date DESC

SELECT d.*, m.user_name, r.room_name, c.diary_cate_name, h.diary_hot FROM diary d left outer join member m ON d.user_no = m.user_no LEFT OUTER JOIN room r ON d.room_no = r.room_no LEFT OUTER JOIN diary_category c ON d.diary_cate_no = c.diary_cate_no LEFT OUTER JOIN diary_hot h ON d.room_no = h.room_no WHERE diary_cate_name != '공지사항' AND (
d.room_no = #{item.roomNo}
) ORDER BY d.diary_date DESC

SELECT * FROM room
WHERE user_no = #{userNo}
ORDER BY room_no

SELECT count(*) cnt FROM diary_notice_read
WHERE user_no = #{userNo}
AND notice_read = 1

		SELECT diary_no FROM (
    		SELECT rownum rnum, diary_no FROM (
      			SELECT diary_no FROM diary
      			WHERE user_no = #{userNo}
        		ORDER BY diary_no DESC
   			) b
		) board WHERE rnum BETWEEN 1 and 1

SELECT count(*) FROM ( SELECT d.*, m.user_name, r.room_name, c.diary_cate_name, h.diary_hot FROM diary d left outer join member m ON d.user_no = m.user_no LEFT OUTER JOIN room r ON d.room_no = r.room_no LEFT OUTER JOIN diary_category c ON d.diary_cate_no = c.diary_cate_no LEFT OUTER JOIN diary_hot h ON d.room_no = h.room_no WHERE d.user_no = #{userNo}
AND d.diary_title like '%'||#{searchtext}||'%'
AND c.diary_cate_name like '%'||#{searchtext}||'%'
AND r.room_name like '%'||#{searchtext}||'%'
AND m.user_name like '%'||#{searchtext}||'%'
) CNT

SELECT * FROM ( SELECT rownum rnum, B.* FROM ( SELECT d.*, m.user_name, r.room_name, c.diary_cate_name, h.diary_hot FROM diary d left outer join member m ON d.user_no = m.user_no LEFT OUTER JOIN room r ON d.room_no = r.room_no LEFT OUTER JOIN diary_category c ON d.diary_cate_no = c.diary_cate_no LEFT OUTER JOIN diary_hot h ON d.room_no = h.room_no WHERE d.user_no = #{userNo}
AND d.diary_title like '%' || #{searchtext} || '%'
AND c.diary_cate_name like '%${searchtext}%'
AND r.room_name like '%${searchtext}%'
AND m.user_name like '%${searchtext}%'
ORDER BY d.diary_date DESC
ORDER BY c.diary_cate_name
ORDER BY r.room_name
ORDER BY m.user_name
) B ) diary WHERE rnum BETWEEN #{startNo} AND #{endNo}

SELECT count(*) FROM room_list
WHERE room_no = #{roomNo}
AND user_no = #{userNo}
